import React, { useEffect, useState } from "react";
import gsap, { Expo, Back } from "gsap";
import Timer from "../../components/Timer";

const Auction = () => {
  const [single, setSingle] = useState({});
  var tl = gsap.timeline();

  useEffect(() => {
    fetch(`https://jsonplaceholder.typicode.com/posts/1`)
      .then((response) => {
        return response.json();
      })
      .then((json) => {
        setSingle(json);
      })
      .then(() => {
        gsap.from(".main-img", { duration: 1, opacity: 0, scale: 1.2 });
      });
  }, []);

  return (
    <div className=' flex bg-gray-200 rounded-lg p-6'>
      <div className='w-1/2 pr-6'>
        <img
          className='main-img rounded-lg shadow-2xl '
          src='/images/item.png'
        ></img>
      </div>
      <div className='w-1/2 bg-white rounded-lg p-4'>
        <Timer date='2020-09-01T01:02:03' />
        <h1 className='h text-purple-500 text-xl'>{single.title}</h1>
        <p className='p'>{single.body}</p>
      </div>
    </div>
  );
};

export default Auction;
