import React, { useEffect, useState } from "react";
import gsap, { Expo, Back } from "gsap";
import Countdown from "react-countdown";

export default function Post() {
  const [single, setSingle] = useState({});
  var tl = gsap.timeline();

  useEffect(() => {
    fetch(`https://jsonplaceholder.typicode.com/posts/1`)
      .then((response) => {
        return response.json();
      })
      .then((json) => {
        setSingle(json);
      })
      .then(() => {});
  }, []);

  const renderer = ({ days, hours, minutes, seconds, completed }) => {
    if (completed) {
      // Render a completed state
      return <Completionist />;
    } else {
      // Render a countdown
      return (
        <div className='text-white'>
          {days}:{hours}:{minutes}:{seconds}
        </div>
      );
    }
  };
  return (
    <div className=' flex bg-gray-200 rounded-lg p-6'>
      <div className='w-1/2 pr-6'>
        <img className='rounded-lg shadow-2xl ' src='/images/item.png'></img>
      </div>
      <div className='w-1/2 bg-white rounded-lg p-4'>
        <Countdown date={"2020-09-01T01:02:03"} renderer={renderer} />
        <h1 className='h text-purple-500 text-xl'>{single.title}</h1>
        <p className='p'>{single.body}</p>
      </div>
    </div>
  );
}
