import { PageTransition } from "next-page-transitions";

const wrapper = {
  margin: "auto",
};
const inner = {
  margin: "auto",
  maxWidth: "1280px",
};

const Layout = (props) => (
  <div style={wrapper} className='margin-auto'>
    <PageTransition timeout={200} classNames='page-transition'>
      <div style={inner}>{props.children}</div>
    </PageTransition>
  </div>
);

export default Layout;
