import React, { useEffect, useState } from "react";
import Layout from "../../components/Layout";
import gsap, { Expo, Back } from "gsap";

export default function Post() {
  const [single, setSingle] = useState({});
  var tl = gsap.timeline();

  useEffect(() => {
    fetch(`https://jsonplaceholder.typicode.com/posts/1`)
      .then((response) => {
        return response.json();
      })
      .then((json) => {
        setSingle(json);
      })
      .then(() => {});
  }, []);
  return (
    <div className=' flex bg-gray-200 rounded-lg p-4'>
      <div className='w-1/2 pr-6'>
        <img
          className='rounded-lg shadow-xl transform hover:scale-105 duration-100'
          src='/images/item.png'
        ></img>
      </div>
      <div className='w-1/2 bg-white rounded-lg shadow-2xl p-4'>
        <h1 className='h text-purple-500 text-xl'>{single.title}</h1>
        <p className='p'>{single.body}</p>
      </div>
    </div>
  );
}
